import React from 'react';
import './card.css';
import PeopleChanges from './PeopleChanges';

class DisplayCard extends React.Component {
	constructor(props) {
		super(props);
		this.state = { currentPerson: 0, Person: {} };
	}

	componentDidMount = (person) => {
		this.setState({ Person: person });
	};
	nextPerson = (person, amtPpl) => {
		if (this.state.currentPerson < amtPpl - 1) {
			this.setState({ currentPerson: this.state.currentPerson + 1, Person: person });
		} else {
			this.setState({ currentPerson: 0 });
		}
	};

	previousPerson = (person, amtPpl) => {
		if (this.state.currentPerson > 0) {
			this.setState({ currentPerson: this.state.currentPerson - 1, Person: person });
		} else {
			this.setState({ currentPerson: amtPpl - 1 });
		}
	};

	render() {
		return (
			<div className="cardContainer">
				<div className="imgContainer">
					<img src={this.state.Person.img} />
					<i className="quote right icon" />
				</div>
				<h2 className="personName">{this.state.Person.name}</h2>
				<h3 className="personJob">{this.state.Person.job}</h3>
				<p className="personDescription">{this.state.Person.description}</p>
				<PeopleChanges
					onLoad={this.componentDidMount}
					currentPerson={this.state.currentPerson}
					next={this.nextPerson}
					previous={this.previousPerson}
				/>
			</div>
		);
	}
}

export default DisplayCard;
