import React from 'react';
import faker from 'faker';
import './card.css';

const People = [
	{
		name: 'Susan Smith',
		job: 'Web Developer',
		img: 'person-1_rfzshl.jpg',
		description:
			"I'm baby meggings twee health goth +1. Bicycle rights tumeric chartreuse before they sold out chambray pop-up. Shaman humblebrag pickled coloring book salvia hoodie, cold-pressed four dollar toast everyday carry"
	},
	{
		name: 'Anna Johnson',
		job: 'Web Designer',
		img: 'person-2_np9x5l.jpg',
		description:
			'Helvetica artisan kinfolk thundercats lumbersexual blue bottle. Disrupt glossier gastropub deep v vice franzen hell of brooklyn twee enamel pin fashion axe.photo booth jean shorts artisan narwhal.'
	},
	{
		name: 'Peter Jones',
		job: 'Intern',
		img: 'person-3_ipa0mj.jpg',
		description:
			'Edison bulb put a bird on it humblebrag, marfa pok pok heirloom fashion axe cray stumptown venmo actually seitan. VHS farm-to-table schlitz, edison bulb pop-up 3 wolf moon tote bag street art shabby chic.'
	},
	{
		name: 'Bill Anderson',
		job: 'CEO',
		img: 'person-4_t9nxjt.jpg',
		description:
			'Sriracha literally flexitarian irony, vape marfa unicorn. Glossier tattooed 8-bit, fixie waistcoat offal activated charcoal slow-carb marfa hell of pabst raclette post-ironic jianbing swag.'
	}
];

const DisplayCard = () => {
	return (
		<div className="cardContainer">
			<div className="imgContainer">
				<img src="person-1_rfzshl.jpg" />
				<i className="quote right icon" />
			</div>
			<h2 className="personName">{People[0].name}</h2>
			<h3 className="personJob">{People[0].job}</h3>
			<p className="personDescription">{People[0].description}</p>
		</div>
	);
};

export default DisplayCard;
